	[3114] = {
		"Glyphic Memorandum",
		{ "While you were helping me out, this memorandum was given to me to pass on to you. Take some time to read it when you have a chance. I'm thinkin' it came from the mage trainer Marryk. Take a gander at it and go find him inside Anvilmar when you've a chance.", },
