	},
	[3115] = {
		"Tainted Memorandum",
		{ "While you were helping me out, this memorandum was given to me to pass on to you. Take some time to read it when you have a chance. I'm thinkin' it came from the warlock trainer Alamar. Take a gander at it and go find him inside Anvilmar when you've a chance.", "And watch yerself, <name>, your kind ain't too trusted 'round these parts.", },
		{ "Read the Tainted Memorandum and speak to Alamar Grimm inside Anvilmar above Coldridge Valley.", },
	},
	[3116] = {
		"Simple Sigil",
		{ "This sigil was given to me by a messenger from our warrior trainer, Alyissia. It seems Alyissia would have words with you when you have a moment. Read it and bring it to her afterwards.", },
		{ "Read the Simple Sigil and speak to Alyissia in Shadowglen.", },
	},
	[3117] = {
		"Etched Sigil",
		{ "This sigil was given to me by a messenger from our hunter trainer, Ayanna. It seems Ayanna would have words with you when you have a moment. Read it and bring it to her afterwards.", },
		{ "Read the Etched Sigil and speak to Ayanna Everstride at the top of Aldrassil in Shadowglen.", },
	},
	[3118] = {
		"Encrypted Sigil",
		{ "This sigil was given to me by a messenger from our rogue trainer, Frahun. It seems Frahun would have words with you when you have a moment. Read it and bring it to him afterwards.", },
