	[7828] = {
		"Stalking the Stalkers",
		{ "Many brave trolls have been lost to the harsh wilds of the Hinterlands, <race>. The Silvermane wolves are one of the biggest threats to our hunters. They lay in wait, hiding amongst the bushes and landscape, then strike out when the opportunity presents itself.", "This type of ferocity will not go unchecked!", "You must think like the Silvermane. Stalk the stalkers! Slay them all!", },
		{ "Huntsman Markhor at Revantusk Village in the Hinterlands wants you to kill 10 Silvermane Stalkers and 10 Silvermane Howlers. Return to him once the task is complete.", "Markhor mentioned that the wolves hide in the wilds of the Hinterlands.", },
	},
	[7829] = {
		"Hunt the Savages",
		{ "Among the more ornery of the wildlife of the Hinterlands are the savage owlbeasts. Many a good Revantusk warrior has been lost to the ferocious claws and beak of the beasts.", "Kill every savage owlbeast you see, <race>. Return to me when you can stand no more bloodshed. Your contribution will then be weighed, measured, and judged.", },
		{ "Huntsman Markhor at Revantusk Village in the Hinterlands wants you to kill 20 Savage Owlbeasts. Return to him once the task is complete.", "Markhor mentioned that the Savage Owlbeasts occupy the wilds of the Hinterlands.", },
	},
	[7830] = {
		"Avenging the Fallen",
		{ "The filth that calls itself Wildhammer trains gryphon to slay troll. Horrible, I know, but worry not, justice will soon be meted out.", "For now, I want you to focus on eradicating the elder gryphons of the region. They are known as Skylords.", "Slay one and return to me with its plume.", },
		{ "Huntsman Markhor at Revantusk Village in the Hinterlands wants you to kill a Razorbeak Skylord and retrieve its Skylord Plume. Return to him once the task is complete.", "Markhor mentioned that the Razorbeak Skylord are a rare breed that tend to occupy the wilds of the Hinterlands.", },
	},
