	[8149] = {
		"Honoring a Hero",
		{ "During the Harvest Festival of Azeroth, we give thanks to heroes for the sacrifices - in some cases ultimate sacrifices - they have given on our behalf.  The Alliance acknowledges many heroes, but none weave a more tragic tale than that of Uther Lightbringer.", "Help us honor Uther by taking this offering to his tomb in the Western Plaguelands.  Place it at his statue, and then return to me.  I'll only be here through the end of the festival, so waste no time in giving Uther his tribute.", },
		{ "Travel to Uther's Tomb in Western Plaguelands and use Uther's Tribute at his statue, and then return to Wagner Hammerstrike outside Ironforge before the Harvest Festival is over.", },
	},
	[8150] = {
		"Honoring a Hero",
		{ "During the Harvest Festival of Azeroth, we give thanks to heroes for the sacrifices - in some cases ultimate sacrifices - they have given on our behalf.  The Horde acknowledges many heroes, but none exemplify strength and sacrifice more than Grom Hellscream.", "Help us honor Grom by taking this offering to his monument in the Demon Fall Canyon of Ashenvale.  Place it at the base, and then return to me.  I'll only be here through the end of the festival, so waste no time in giving Grom his tribute.", },
		{ "Travel to Grom's Monument in the Demon Fall Canyon of Ashenvale and use Grom's Tribute at the base of the monument.", "Return to Javnir Nashak outside Orgrimmar before the Harvest Festival is over.", },
	},
	[8151] = {
		"The Hunter's Charm",
		{ "Ogtinc of Azshara has sent out a call for seasoned hunters such as yourself.  He is a furbolg hunter who has left his tribe to aid the Cenarion Circle.  His teachings are meant to enrich the wisdom of others who share his craft, and guide them toward maintaining a balance.  You would do well to speak with him.", "Ogtinc resides atop the cliffs of Azshara, northeast from the Ruins of Eldarath.", },
		{ "Speak to Ogtinc in Azshara.", },
	},
	[8153] = {
		"Courser Antlers",
		{ "The courser wears the mightiest antlers in the known lands.  Such antlers are used to defend the courser, goring an attacker and often damaging the points.  Perfect, natural antlers are the sign of an animal spirit with supreme confidence.", "To hold such antlers will bestow the same confidence in you.", "Hunt the mosshoof courser, find the perfect antlers and bring them to me.", },
		{ "Bring a pair of Perfect Courser Antlers to Ogtinc in Azshara.", "Ogtinc resides atop the cliffs northeast of the Ruins of Eldarath.", },
	},
