	[8227] = {
		"Nat's Measuring Tape",
		{ "Amongst the shattered fishing lures and tackle is a rolled, silken measuring tape.  Attached to the tape is a label that reads:", "\"Nat Pagle, Dustwallow Marsh.\"", },
		{ "Return Nat's Measuring Tape to Nat Pagle in Dustwallow Marsh.", },
	},
	[8228] = {
