	[8728] = {
		"The Good News and The Bad News",
		{ "Well that took a little longer than expected. Now let me see what this ledger says.", "<Narain silently reads the ledger, periodically mumbling under his breath.>", "The good news is that I am 99% certain that I am capable of creating an arcanite buoy that will function to your specifications. This is, of course, wholly dependant on the bad news.", "The bad news is that I'm going to need a lot of arcanite, elementium ore, and rare gemstones.", "Bring me what I need and I will craft the buoy.", },
		{ "Narain Soothfancy in Tanaris wants you to bring him 20 Arcanite Bars, 10 Elementium Ore, 10 Azerothian Diamonds, and 10 Blue Sapphires.", },
	},
	[8729] = {
		"The Wrath of Neptulon",
		{ "Did I mention the bad news yet? Its name is Maws: 100 feet of death and destruction. I guess I may have forgotten that little tidbit.", "According to my superior psycho-psionic clairvoyance, you'll find that the best spot to cast this buoy is off the coast of Azshara, in the Bay of Storms.", "Look for a swirling maelstrom, most likely cluttered with wreckage.", "By the by, I hope you have friends.", "Now if you somehow miraculously succeed, take the scepter shard to Anachronos.", },
		{ "Use the Arcanite Buoy at the Swirling Maelstrom at the Bay of Storms in Azshara.", },
	},
	[8730] = {
		"Nefarius's Corruption",
		{ "Champion, is it you? A thousand years it has been since I was entrusted the shard and it is at my darkest hour that one should rise to relieve me of it... But what would tragedy be without cruelty?", "<Vaelastrasz coughs weakly.>", "Ne... Nefarius now holds the scepter shard.", "Time is of the essence. Nefarius plans to destroy the shard. You must hurry!", },
		{ "Slay Nefarian and recover the Red Scepter Shard. Return the Red Scepter Shard to Anachronos at the Caverns of Time in Tanaris. You have 5 hours to complete this task.", },
	},
	[8731] = {
		"Field Duty",
		{ "Greetings, <name>.  Before I assign you to more involved tasks in the war, you'll need to do your share of field duty.", "You will find Captain Skullsplit's Orgrimmar Legion outside Hive'Regal.", "Report to him and come back to me with proof of your field service.  I shall have a more advanced assignment ready for you.", "Oh, please do your best to stay away from the Ironforge Brigade.  Tensions are high among our Alliance and Horde volunteers.", },
		{ "Report to Krug Skullsplit at the Orgrimmar Legion post in front of Hive'Regal.", "Prepare your Unsigned Field Duty Papers, obtain Signed Field Duty Papers and bring them to Windcaller Kaldon in Cenarion Hold.", "Note: Healing or casting beneficial spells on a member of the Orgrimmar Legion will flag you for PvP.", },
	},
	[8732] = {
		"Field Duty Papers",
	},
	[8733] = {
		"Eranikus, Tyrant of the Dream",
		{ "This is a journey of redemption, hero. Would it be that I was slain today, I would have died for a just and righteous cause. You must recover the green scepter shard. You must cleanse Eranikus of the taint placed upon him by the Old Gods.", "Travel to Darnassus. Just outside the city walls you will find one of my agents. This agent will set things in motion for us and inform Tyrande of our plans without alerting Staghelm.", "<Malfurion sighs.>", "A fall from grace a thousand years in the making...", },
		{ "Travel to the continent of Teldrassil and find Malfurion's agent somewhere outside the walls of Darnassus.", },
	},
	[8734] = {
		"Tyrande and Remulos",
		{ "Tyrande will be notifed, mortal. Shan'do Stormrage has prepared me for this contingency.", "Now you must venture to the Moonglade and speak with Remulos. He will have further instruction as to the summoning of Eranikus.", "We all wish you great luck.", },
		{ "Travel to the Moonglade and speak to Keeper Remulos. ", },
	},
	[8735] = {
		"The Nightmare's Corruption",
		{ "You must travel to the four dream portals of Azeroth. From each you must draw a fragment of the Nightmare's corruption. The dragonkin that inhabit these areas will hold such fragments. Return to me when you have gathered these fragments.", },
		{ "Travel to the four Emerald Dream portals in Azeroth and collect a Fragment of the Nightmare's Corruption from each. Return to Keeper Remulos in the Moonglade when you have completed this task.", },
	},
	[8736] = {
		"The Nightmare Manifests",
		{ "We must use restraint against Eranikus. We are to redeem him not destroy him.", "Tyrande is already en route to assist us in this task. We must weather the dragon's blows and save Nighthaven from certain destruction.", "Be on guard! You and your allies are our only hope.", },
		{ "Defend Nighthaven from Eranikus. Do not let Keeper Remulos perish. Do not slay Eranikus. Defend yourself. Await Tyrande.", },
	},
	[8737] = {
		"Azure Templar",
		{ "The Twilight's Hammer and the elemental nobles they worship present a formidable threat to our operations in Silithus.  With the new enemies we're facing inside Ahn'Qiraj, we cannot afford to lower our guard to our enemies outside.", "Find a way to summon and destroy an Azure Templar and report to Bor Wildmane.", },
		{ "Summon and slay an Azure Templar and report back to Bor Wildmane in Cenarion Hold.", "You must also bring Tactical Task Briefing I in order to complete this quest.", },
	},
	[8738] = {
		"Hive'Regal Scout Report",
		{ "We've sent our best scouts deep into the silithid hives to gather intelligence.  Scout Landion has been watching the silithid movements at Hive'Regal for several days now.  Find him and obtain a written report.  We need to be able to act on that information as soon as possible, <name>.", },
		{ "Contact Cenarion Scout Landion inside Hive'Regal and return the Hive'Regal Scout Report to Windcaller Proudhorn at Cenarion Hold.", "You must also bring Tactical Task Briefing VII in order to complete this quest.", },
	},
	[8739] = {
		"Hive'Ashi Scout Report",
		{ "We've sent our best scouts deep into the silithid hives to gather intelligence.  Scout Jalia has been watching the silithid movements at Hive'Ashi for several days now.  Find her and obtain a written report.  We need to be able to act on that information as soon as possible, <name>.", },
		{ "Contact Cenarion Scout Jalia inside Hive'Ashi and return the Hive'Ashi Scout Report to Windcaller Proudhorn at Cenarion Hold.", "You must also bring Tactical Task Briefing VIII in order to complete this quest.", },
	},
	[8740] = {
		"Twilight Marauders",
		{ "Bands of mounted Twilight's Hammer cultists have been spotted riding outside the reach of our forces in Cenarion Hold, picking off our smaller patrols and caravans.", "Find and defeat the twilight marauders along with their leader, a terrible warrior by the name of Morna.  Report to Windcaller Proudhorn after completing the task.", },
		{ "Slay Twilight Marauder Morna and 5 Twilight Marauders.", "Report to Windcaller Proudhorn when your task is finished.", "You must also bring Tactical Task Briefing IX in order to complete this quest.", },
	},
	[8741] = {
		"The Champion Returns",
		{ "The keeper awaits your return, <name>. Take the green scepter shard to Anachronos at the Caverns of Time.", },
		{ "Take the Green Scepter Shard to Anachronos at the Caverns of Time in Tanaris.", },
