	[8778] = {
		"The Ironforge Brigade Needs Explosives!",
		{ "The Ironforge Brigade has put in an unusual request for highly... volatile... components.  Gather them and bring them to Mr. Nozzlespring from the Ironforge Brigade.", },
		{ "Bring 6 Oils of Immolation, 5 Goblin Rocket Fuel and 10 Dense Blasting Powder to Arcanist Nozzlespring near Hive'Zora in Silithus.", "You must also bring Logistics Task Briefing IV in order to complete this quest.", },
	},
	[8779] = {
		"Scrying Materials",
		{ "Geologist Larksbane's studies have proven to be of tremendous value to our operations in Silithus.  She has recently requested scrying materials to be used in her research.", "Please gather these materials and deliver them to her directly so that she may continue her research.", },
		{ "Bring 1 Large Brilliant Shard, 1 Large Radiant Shard and 1 Huge Emerald to Geologist Larksbane at Cenarion Hold in Silithus.", "You must also bring Logistics Task Briefing V in order to complete this quest.", },
	},
	[8780] = {
		"Armor Kits for the Field",
		{ "The Ironforge Brigade's supply of armor kits in the battlefield is running dangerously low.  Obtain a batch and deliver them to Janela Stouthammer at the Ironforge Brigade's outpost outside Hive'Zora.", },
		{ "Bring 8 Rugged Armor Kits and 8 Heavy Armor Kits to Janela Stouthammer at the Ironforge Brigade Outpost near Hive'Zora in Silithus.", "You must also bring Logistics Task Briefing VII in order to complete this quest.", },
	},
	[8781] = {
		"Arms for the Field",
		{ "The Ironforge Brigade has been a worthy ally in our fight in Silithus.  Unfortunately, many of their supplies were lost on their way here.  Procure a batch of weapons and deliver them to Janela Stouthammer near Hive'Zora.", },
		{ "Bring 2 Moonsteel Broadswords to Janela Stouthammer at the Ironforge Brigade Outpost outside of Hive'Zora.", "You must also bring Logistics Task Briefing VI in order to complete this quest.", },
	},
	[8782] = {
		"Uniform Supplies",
		{ "The latest influx of volunteers has caused a shortage of uniforms.  Procure a batch of uniform materials and bring them to Windcaller Proudhorn.", },
		{ "Bring 1 Mooncloth, 2 Bolts of Runecloth and 1 Ironweb Spider Silk to Windcaller Proudhorn at Cenarion Hold in Silithus.", "You must also bring Logistics Task Briefing VIII in order to complete this quest.", },
	},
	[8783] = {
		"Extraordinary Materials",
		{ "Vargus, Cenarion Hold's blacksmith, has requested enchanted materials to be used in the crafting of powerful weapons for our forces.  Obtain these and bring them directly to Vargus so that Cenarion Hold's troops may continue to be well equipped.", },
		{ "Bring 2 Enchanted Thorium Bars and 2 Enchanted Leather to Vargus at Cenarion Hold in Silithus.", "You must also bring Logistics Task Briefing IX in order to complete this quest.", },
	},
	[8784] = {
		"Secrets of the Qiraji",
		{ "You have discovered an ancient Qiraji artifact! Perhaps one of the dragons near the entrance of the temple would have a use for this relic.", },
		{ "Take the Ancient Qiraji Artifact to the dragons hiding near the entrance of the temple.", },
	},
	[8785] = {
		"The Orgrimmar Legion Needs Mojo!",
		{ "The Orgrimmar Legion has put in a request for several unusual ingredients.  Given their current performance in Silithus, we'd like to comply with their request as soon as possible.  Gather the materials and bring them to the troll called Shai.", },
		{ "Bring 6 Powerful Mojo, 6 Flasks of Big Mojo and 8 Oils of Immolation to Shadow Priestess Shai near Hive'Regal in Silithus.", "You must also bring Logistics Task Briefing IV in order to complete this quest.", },
	},
	[8786] = {
		"Arms for the Field",
		{ "The Orgrimmar Legion has put in a request for additional weapons that we haven't been able to provide at the moment.  Obtain the weapons they've requested and bring them to Merok Longstride outside of Hive'Regal.", },
		{ "Bring 3 Massive Iron Axes to Merok Longstride at the Orgrimmar Legion camp outside of Hive'Regal.", "You must also bring Logistics Task Briefing VI in order to complete this quest.", },
	},
	[8787] = {
		"Armor Kits for the Field",
		{ "The brave volunteers of the Orgrimmar Legion have arrived to Silithus to aid us in our cause.  Their supplies of armor kits were somehow lost in the trip.  Obtain a batch of armor kits and deliver them to Merok Longstride at the location of the Orgrimmar Legion camp.", },
		{ "Bring 8 Rugged Armor Kits and 8 Heavy Armor Kits to Merok Longstride near Hive'Regal.", "You must also bring Logistics Task Briefing VII in order to complete this quest.", },
	},
	[8788] = {
		"A Gently Shaken Gift",
	},
	[8789] = {
		"Imperial Qiraji Armaments",
		{ "We have not sat idly by for a millennium, <name>.", "We have watched these beasts feast on their own when they have exhausted their utility. We have listened as they have plotted and schemed to destroy our world. And we have learned of their own internal treachery.", "Weaknesses exist and we know how to use those weaknesses to our advantage.", "Find and bring me their ceremonial armaments: Combined with the element created from the volatility of their own God, they will become a force of reckoning.", },
		{ "Arygos in the Temple of Ahn'Qiraj will create Elementium Infused Armaments for you should you bring him Imperial Qiraji Armaments and 3 Elementium Ore.", },
	},
	[8790] = {
		"Imperial Qiraji Regalia",
		{ "Arygos theorizes that the element which is born of conflicts between the Old Gods and their minions is also extremely deadly to them under the right conditions. I speak of Elementium, mortal.", "Find and bring me Imperial Qiraji Regalia and the toxic element and I shall infuse weapons of great power.", },
		{ "Merithra of the Dream in the Temple of Ahn'Qiraj will create Elementium Infused Armaments for you should you bring her Imperial Qiraji Regalia and 3 Elementium Ore.", },
	},
	[8791] = {
		"The Fall of Ossirian",
		{ "A terrible menace has been laid to rest. The cruel Ossirian has been vanquished.", "The denizens of Kalimdor would be relieved to know of this victory. Take Ossirian's head and present it to Commander Mar'alith of Cenarion Hold.", },
		{ "Deliver the Head of Ossirian the Unscarred to Commander Mar'alith at Cenarion Hold in Silithus.", },
	},
	[8792] = {
		"The Horde Needs Your Help!",
		{ "Hello, I'm glad that you've decided to hear me out. The Horde needs all of the help that it can get to prepare for the Ahn'Qiraj War, and that means that we need you! Even now as we speak, official collectors are gathering the necessary material needed for the upcoming war, but we won't be able to meet our goals without your assistance, <name>!", "You should go speak with the guy in charge, Warlord Gorchuk. What do you say, <class>? Will you help out with the vital preparations?", },
		{ "Speak with Warlord Gorchuk in Orgrimmar's Valley of Spirits.", },
	},
	[8793] = {
		"The Horde Needs Your Help!",
