	[8799] = {
		"The Hero of the Day",
		{ "Listen... I'm not really supposed to be telling you this, but since you're the hero of the day and all...", "There's fresh holly everywhere this time of the year, but it never lasts.  We've got a machine that preserves holly - it never goes bad!  We use it to spice up the holiday... and profits during the summer!", "I'll let you use the preserver, but only if you're a master cook.  You'll need some deeprock salt and two, three, oh let's say five gold.  It'll net you a nice batch that's yours to keep.", },
		{ "Bring 1 Deeprock Salt and 5 gold coins to the Holly Preserver machine, upon which you will receive 5 Preserved Holly.", },
	},
	[8800] = {
		"Cenarion Battlegear",
		{ "As you complete the tasks you're assigned in the war you'll want to talk to Vargus, the blacksmith.  Show him any badges you've earned along the way. He'll provide you with gear to match your level of commitment to our cause.", },
		{ "Talk to Vargus at Cenarion Hold in Silithus.", },
	},
	[8801] = {
		"C'Thun's Legacy",
		{ "The walls of Ahn'Qiraj tremble. A force of evil, older than the world itself, has been destroyed.", "As you look at the remnants of the colossal abomination your heart nearly freezes. Even in death you can feel the legacy of C'Thun's evil around you.", "You have done what was thought to be impossible.", "You grab at an eye stalk of the fiend, stowing it safely in your pack.", "Take the Eye of C'Thun to the dragons in the next chamber. ", },
		{ "Take the Eye of C'Thun to Caelastrasz in the Temple of Ahn'Qiraj.", },
	},
	[8802] = {
		"The Savior of Kalimdor",
		{ "To the Caverns of Time you go, <Lord/Lady> <name>. Anachronos awaits your return. Give him the eye of C'Thun as he will undoubtedly wish to place it in his master's collection.", },
		{ "Take the Eye of C'Thun to Anachronos at the Caverns of Time.", },
	},
	[8803] = {
		"A Festive Gift",
	},
	[8804] = {
		"Desert Survival Kits",
		{ "With the arrival of fresh recruits to Cenarion Hold comes the problem of keeping them alive in the desert.  Dehydration as well as numerous poisonous creatures present a serious threat to inexperienced volunteers.  Gather the supplies necessary for a desert survival kit and bring them to Calandrath in Cenarion Hold.", },
		{ "Bring 4 Globes of Water, 4 Powerful Anti-Venom and 4 Smoked Desert Dumplings to Calandrath at the inn in Cenarion Hold in Silithus.", "You must also bring Logistics Task Briefing I in order to complete this quest.", },
	},
	[8805] = {
		"Boots for the Guard",
		{ "The mounted division of the Cenarion Hold guard is in short supply of ornate mithril boots.  Procure a batch and deliver them immediately to Captain Vish Kozus in Cenarion Hold.", },
		{ "Bring 3 Ornate Mithril Boots to Vish Kozus, Captain of the Guard at Cenarion Hold in Silithus.", "You must also bring Logistics Task Briefing II in order to complete this quest.", },
	},
	[8806] = {
		"Grinding Stones for the Guard",
		{ "The Cenarion Hold guard is in short supply of grinding stones.  Procure a batch and deliver them immediately to Captain Vish Kozus in Cenarion Hold.", },
		{ "Bring 10 Dense Grinding Stones, 10 Solid Grinding Stones and 10 Heavy Grinding Stones to Vish Kozus, Captain of the Guard at Cenarion Hold in Silithus.", "You must also bring Logistics Task Briefing III in order to complete this quest.", },
	},
	[8807] = {
