	[9211] = {
		"The Ice Guard",
		{ "Ah, the Ice Guard. It is a thin sheen of protective magic that covers certain pieces of equipment. Those that do battle with creatures of frost and ice will gain increased protection from their attacks by using the Ice Guard.", "As with all things the Wrathcaster creates, there is a price - a price that I will not lower, regardless of how much you whimper. Interested?", },
		{ "Mataus the Wrathcaster at Light's Hope Chapel in the Eastern Plaguelands wants 10 Insignia of the Crusade and 30 gold.", },
	},
	[9213] = {
		"The Shadow Guard",
		{ "A creation of genius! Much like the Ice Guard, the Shadow Guard employs a thin sheen of protective magic that covers certain pieces of equipment. And like the Ice Guard, the Shadow Guard is going to cost you a good amount of money.", "<Mataus holds his hand out and rubs his fingers together.>", "Interested?", },
		{ "Mataus the Wrathcaster at Light's Hope Chapel in the Eastern Plaguelands wants 10 Insignia of the Crusade and 30 gold.", },
	},
	[9221] = {
		"Superior Armaments of Battle - Friend of the Dawn",
		{ "Greetings, friend. Have you insignias of the Dawn or the Crusade that you would like to redeem for items of power?", "I must warn you; purchases made at a friendly reputation with the Dawn come with a hefty surcharge. I would advise that you gain a better reputation with the Dawn before moving forward with your plans.", },
		{ "For 30 Insignias of the Dawn and 30 Insignias of the Crusade you may choose an item from the Argent Dawn's treasure cache.", },
	},
	[9222] = {
